$('.alert-logout').on('click', function() {
    var getLink = $(this).attr('href');
    swal.fire({
            title: 'Konfirmasi Log Out !',
            html: 'Anda Akan Keluar Dari Aplikasi, Lanjutkan ?',
            icon: "warning",
            showCancelButton: true,
        })
        .then((result) => {
            if (result.isConfirmed) {
                window.location.href = getLink
            }
        });
    return false;
});

